import React, { useState } from 'react';

const RegistroPaciente = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [cedula, setCedula] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [nacionalidad, setNacionalidad] = useState('');
  const [paisNacimiento, setPaisNacimiento] = useState('');
  const [estadoCivil, setEstadoCivil] = useState('');
  const [sexo, setSexo] = useState('');
  const [peso, setPeso] = useState('');
  const [altura, setAltura] = useState('');
  const [padecimiento, setPadecimiento] = useState('');
  const [fechaUltimaVisita, setFechaUltimaVisita] = useState('');
  const [diagnostico, setDiagnostico] = useState('');
  const [medicoCabecera, setMedicoCabecera] = useState('');
  const [especialidad, setEspecialidad] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simular guardado de datos
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 3000); // Ocultar popup después de 3 segundos
  };

  return (
    <div className="registro-paciente">
      <h2>Registro de Paciente</h2>
      <form onSubmit={handleSubmit}>
        {/* Sección 1: Datos Generales */}
        <fieldset>
          <legend>Datos Generales</legend>
          <label>
            Nombre:
            <input
              type="text"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </label>
          <label>
            Apellido:
            <input
              type="text"
              value={apellido}
              onChange={(e) => setApellido(e.target.value)}
              required
            />
          </label>
          <label>
            Cédula:
            <input
              type="text"
              value={cedula}
              onChange={(e) => setCedula(e.target.value)}
              required
            />
          </label>
          <label>
            Fecha de Nacimiento:
            <input
              type="date"
              value={fechaNacimiento}
              onChange={(e) => setFechaNacimiento(e.target.value)}
              required
            />
          </label>
          <label>
            Nacionalidad:
            <select
              value={nacionalidad}
              onChange={(e) => setNacionalidad(e.target.value)}
              required
            >
              <option value="">Seleccione</option>
              <option value="Mexicana">Mexicana</option>
              <option value="Colombiana">Colombiana</option>
              <option value="Española">Española</option>
            </select>
          </label>
          <label>
            País de Nacimiento:
            <select
              value={paisNacimiento}
              onChange={(e) => setPaisNacimiento(e.target.value)}
              required
            >
              <option value="">Seleccione</option>
              <option value="México">México</option>
              <option value="Colombia">Colombia</option>
              <option value="España">España</option>
            </select>
          </label>
          <label>
            Estado Civil:
            <select
              value={estadoCivil}
              onChange={(e) => setEstadoCivil(e.target.value)}
              required
            >
              <option value="">Seleccione</option>
              <option value="Soltero">Soltero</option>
              <option value="Casado">Casado</option>
              <option value="Divorciado">Divorciado</option>
              <option value="Viudo">Viudo</option>
            </select>
          </label>
        </fieldset>

        {/* Sección 2: Perfil del Paciente */}
        <fieldset>
          <legend>Perfil del Paciente</legend>
          <label>
            Sexo:
            <div>
              <label>
                <input
                  type="radio"
                  name="sexo"
                  value="Masculino"
                  checked={sexo === 'Masculino'}
                  onChange={(e) => setSexo(e.target.value)}
                /> Masculino
              </label>
              <label>
                <input
                  type="radio"
                  name="sexo"
                  value="Femenino"
                  checked={sexo === 'Femenino'}
                  onChange={(e) => setSexo(e.target.value)}
                /> Femenino
              </label>
              <label>
                <input
                  type="radio"
                  name="sexo"
                  value="Otro"
                  checked={sexo === 'Otro'}
                  onChange={(e) => setSexo(e.target.value)}
                /> Otro
              </label>
              <label>
                <input
                  type="radio"
                  name="sexo"
                  value="Prefiero no decir"
                  checked={sexo === 'Prefiero no decir'}
                  onChange={(e) => setSexo(e.target.value)}
                /> Prefiero no decir
              </label>
            </div>
          </label>
          <label>
            Peso (kg):
            <input
              type="number"
              value={peso}
              onChange={(e) => setPeso(e.target.value)}
              required
            />
          </label>
          <label>
            Altura (cm):
            <input
              type="number"
              value={altura}
              onChange={(e) => setAltura(e.target.value)}
              required
            />
          </label>
          <label>
            Padecimiento Diagnosticado:
            <input
              type="text"
              value={padecimiento}
              onChange={(e) => setPadecimiento(e.target.value)}
              required
            />
          </label>
          <label>
            Fecha de Última Visita:
            <input
              type="date"
              value={fechaUltimaVisita}
              onChange={(e) => setFechaUltimaVisita(e.target.value)}
              required
            />
          </label>
        </fieldset>

        {/* Sección 3: Detalle de Padecimientos */}
        <fieldset>
          <legend>Detalle de Padecimientos</legend>
          <label>
            Diagnóstico:
            <input
              type="text"
              value={diagnostico}
              onChange={(e) => setDiagnostico(e.target.value)}
              required
            />
          </label>
          <label>
            Médico de Cabecera:
            <input
              type="text"
              value={medicoCabecera}
              onChange={(e) => setMedicoCabecera(e.target.value)}
              required
            />
          </label>
          <label>
            Especialidad del Médico:
            <select
              value={especialidad}
              onChange={(e) => setEspecialidad(e.target.value)}
              required
            >
              <option value="">Seleccione</option>
              <option value="Cardiología">Cardiología</option>
              <option value="Pediatría">Pediatría</option>
              <option value="Dermatología">Dermatología</option>
            </select>
          </label>
        </fieldset>

        {/* Botón de Guardar */}
        <button type="submit">Guardar</button>
      </form>

      {/* Popup de Confirmación */}
      {showPopup && (
        <div className="popup">
          <p>Su información ha sido actualizada correctamente.</p>
        </div>
      )}
    </div>
  );
};

export default RegistroPaciente;
