import React, { useState } from 'react';

const RegistroMedico = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [cedula, setCedula] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [nacionalidad, setNacionalidad] = useState('');
  const [paisNacimiento, setPaisNacimiento] = useState('');
  const [estadoCivil, setEstadoCivil] = useState('');
  const [sexo, setSexo] = useState('');
  const [padecimientos, setPadecimientos] = useState('');
  const [anoFinalizacion, setAnoFinalizacion] = useState('');
  const [tituloPrincipal, setTituloPrincipal] = useState('');
  const [especialidad1, setEspecialidad1] = useState('');
  const [especialidad2, setEspecialidad2] = useState('');
  const [fotoMedico, setFotoMedico] = useState(null);
  const [especialidadAtencion, setEspecialidadAtencion] = useState('');
  const [diasSemana, setDiasSemana] = useState([]);
  const [horariosAtencion, setHorariosAtencion] = useState('');
  const [numeroEmergencia, setNumeroEmergencia] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simular guardado de datos
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 3000); // Ocultar popup después de 3 segundos
  };

  const handleDiasSemanaChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setDiasSemana([...diasSemana, value]);
    } else {
      setDiasSemana(diasSemana.filter((dia) => dia !== value));
    }
  };

  return (
    <div className="registro-medico">
      <h2>Registro de Médico</h2>
      <form onSubmit={handleSubmit}>
        {/* Sección 1: Datos Generales */}
        <fieldset>
          <legend>Datos Generales</legend>
          <div className="form-row">
            <label>
              Nombre:
              <input
                type="text"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                required
              />
            </label>
            <label>
              Apellido:
              <input
                type="text"
                value={apellido}
                onChange={(e) => setApellido(e.target.value)}
                required
              />
            </label>
          </div>
          <div className="form-row">
            <label>
              Cédula:
              <input
                type="text"
                value={cedula}
                onChange={(e) => setCedula(e.target.value)}
                required
              />
            </label>
            <label>
              Fecha de Nacimiento:
              <input
                type="date"
                value={fechaNacimiento}
                onChange={(e) => setFechaNacimiento(e.target.value)}
                required
              />
            </label>
          </div>
          <div className="form-row">
            <label>
              Nacionalidad:
              <select
                value={nacionalidad}
                onChange={(e) => setNacionalidad(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="Mexicana">Mexicana</option>
                <option value="Colombiana">Colombiana</option>
                <option value="Española">Española</option>
              </select>
            </label>
            <label>
              País de Nacimiento:
              <select
                value={paisNacimiento}
                onChange={(e) => setPaisNacimiento(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="México">México</option>
                <option value="Colombia">Colombia</option>
                <option value="España">España</option>
              </select>
            </label>
          </div>
          <div className="form-row">
            <label>
              Estado Civil:
              <select
                value={estadoCivil}
                onChange={(e) => setEstadoCivil(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="Soltero">Soltero</option>
                <option value="Casado">Casado</option>
                <option value="Divorciado">Divorciado</option>
                <option value="Viudo">Viudo</option>
              </select>
            </label>
          </div>
        </fieldset>

        {/* Sección 2: Perfil del Médico */}
        <fieldset>
          <legend>Perfil del Médico</legend>
          <div className="form-row">
            <label>
              Sexo:
              <div>
                <label>
                  <input
                    type="radio"
                    name="sexo"
                    value="Masculino"
                    checked={sexo === 'Masculino'}
                    onChange={(e) => setSexo(e.target.value)}
                  /> Masculino
                </label>
                <label>
                  <input
                    type="radio"
                    name="sexo"
                    value="Femenino"
                    checked={sexo === 'Femenino'}
                    onChange={(e) => setSexo(e.target.value)}
                  /> Femenino
                </label>
                <label>
                  <input
                    type="radio"
                    name="sexo"
                    value="Otro"
                    checked={sexo === 'Otro'}
                    onChange={(e) => setSexo(e.target.value)}
                  /> Otro
                </label>
                <label>
                  <input
                    type="radio"
                    name="sexo"
                    value="Prefiero no decir"
                    checked={sexo === 'Prefiero no decir'}
                    onChange={(e) => setSexo(e.target.value)}
                  /> Prefiero no decir
                </label>
              </div>
            </label>
          </div>
          <div className="form-row">
            <label>
              Padecimientos:
              <input
                type="text"
                value={padecimientos}
                onChange={(e) => setPadecimientos(e.target.value)}
                required
              />
            </label>
            <label>
              Año de Finalización de Carrera:
              <input
                type="number"
                value={anoFinalizacion}
                onChange={(e) => setAnoFinalizacion(e.target.value)}
                required
              />
            </label>
          </div>
          <div className="form-row">
            <label>
              Título Principal:
              <input
                type="text"
                value={tituloPrincipal}
                onChange={(e) => setTituloPrincipal(e.target.value)}
                required
              />
            </label>
          </div>
          <div className="form-row">
            <label>
              Especialidad 1:
              <select
                value={especialidad1}
                onChange={(e) => setEspecialidad1(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="Cardiología">Cardiología</option>
                <option value="Pediatría">Pediatría</option>
                <option value="Dermatología">Dermatología</option>
              </select>
            </label>
            <label>
              Especialidad 2:
              <select
                value={especialidad2}
                onChange={(e) => setEspecialidad2(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="Cardiología">Cardiología</option>
                <option value="Pediatría">Pediatría</option>
                <option value="Dermatología">Dermatología</option>
              </select>
            </label>
          </div>
          <div className="form-row">
            <label>
              Foto del Médico:
              <input
                type="file"
                onChange={(e) => setFotoMedico(e.target.files[0])}
                accept="image/*"
                required
              />
            </label>
          </div>
        </fieldset>

        {/* Sección 3: Áreas de Atención */}
        <fieldset>
          <legend>Áreas de Atención</legend>
          <div className="form-row">
            <label>
              Especialidad:
              <select
                value={especialidadAtencion}
                onChange={(e) => setEspecialidadAtencion(e.target.value)}
                required
              >
                <option value="">Seleccione</option>
                <option value="Cardiología">Cardiología</option>
                <option value="Pediatría">Pediatría</option>
                <option value="Dermatología">Dermatología</option>
              </select>
            </label>
          </div>
          <div className="form-row">
            <label>
              Días por Semana:
              <div>
                {['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'].map((dia) => (
                  <label key={dia}>
                    <input
                      type="checkbox"
                      value={dia}
                      checked={diasSemana.includes(dia)}
                      onChange={handleDiasSemanaChange}
                    /> {dia}
                  </label>
                ))}
              </div>
            </label>
          </div>
          <div className="form-row">
            <label>
              Horarios de Atención Semanal:
              <input
                type="text"
                value={horariosAtencion}
                onChange={(e) => setHorariosAtencion(e.target.value)}
                placeholder="Ej: 09:00 - 17:00"
                required
              />
            </label>
          </div>
          <div className="form-row">
            <label>
              Número de Emergencia:
              <input
                type="text"
                value={numeroEmergencia}
                onChange={(e) => setNumeroEmergencia(e.target.value)}
                required
              />
            </label>
          </div>
        </fieldset>

        {/* Botón de Guardar */}
        <button type="submit">Guardar</button>
      </form>

      {/* Popup de Confirmación */}
      {showPopup && (
        <div className="popup">
          <p>Su información ha sido actualizada correctamente.</p>
        </div>
      )}
    </div>
  );
};

export default RegistroMedico;
